// 알파벳 출력 => ABCD~Z => 가 ~ 힣
public class 제어문_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int num = 1; num <= 10; num+=2) {
			System.out.println(num);
		}
		System.out.println();
		for(int i=1; i<=10; i++) {
			System.out.println(i);
		}
		System.out.println();
		for(int i=2; i<=10; i+=2) {
			System.out.println(i);
		}
		// for(;;) // 무한 루프 => 게임
	}

}
